$(document).ready(function(){$(document).delegate("div.tag-options-icon","mouseenter",function(){$elem=$(this).parent().next("div");$elem.slideDown();$(this).mouseleave(function(){var n=setTimeout(function(){$elem.slideUp()},1e3);$elem.hover(function(){clearTimeout(n)})});$elem.mouseleave(function(){setTimeout(function(){$elem.slideUp()},1e3)})})});